/**
 * Write a c++ function to find binomial co-efficient.
 * > binomialCoeff(5, 2)
 * 10
 * > binomialCoeff(4, 3)
 * 4
 * > binomialCoeff(3, 2)
 * 3
 */
int binomialCoeff(int n, int k) {
{
  if(k < 0 || n < 0 || n < k) {
    return -1;
  }
  if(k == 0 || k == n) {
    return 1;
  }
  if(k > n/2) {
    k = n-k;
  }
  return binomialCoeff(n-1, k-1) + binomialCoeff(n-1, k);
}

canonical_solution:
import java.io.*;
import java.lang.*;
import java.util.*;
import java.math.*;


class BinomialCoeff {
    /**
     * * Write a Java function to find binomial co-efficient.
     *
     * > binomialCoeff(5, 2)
     * 10
     * > binomialCoeff(4, 3)
     * 4
     * > binomialCoeff(3, 2)
     * 3
     */
    public static int binomialCoeff(int n, int k) {
        if (k == 0 || k == n) {
            return 1;
        }
        if (k > n - k) {
            k = n - k;
        }
        int b = 1;
        for (int i = 1; i <= k; i++) {
            b = b * (n - i + 1) / i;
        }
        return b;
    }
}

