        for (int i = 0; i < s.length(); i++) {
            int left = 0;
            int right = counts[s.charAt(i) - 'a'];
            while (left <= right) {
                total += counts[s.charAt(i) - 'a'];
                counts[s.charAt(i) - 'a']--;
                left++;
            }
        }
        return total;
    }
}

