     * 23
     */
    public static int maxDifference(List<List<Integer>> testList) {
        int max = Integer.MIN_VALUE;
        for (List<Integer> list : testList) {
            int maxdiff = 0;
            for (int i : list) {
                for (int j : list) {
                    maxdiff = Math.max(maxdiff, Math.abs(i - j));
                }
            }
            max = Math.max(max, maxdiff);
        }
        return max;
    }
}

