        for (int i = 0; i < arr.size(); i++) {
            freq.put(arr.get(i), freq.getOrDefault(arr.get(i), 0) + 1);
        }
        int ans = 0;
        for (Map.Entry<Integer, Integer> entry : freq.entrySet()) {
            if (entry.getValue() == 1) {
                if (ans == 0) {
                    ans = entry.getKey();
                } else {
                    return -1;
                }
            }
        }
        return ans;
    }
}

