import java.io.*;
import java.lang.*;
import java.util.*;
import java.math.*;


class LargestTriangle {
    /**
     * * Write a function to find the largest triangle that can be inscribed in an ellipse.
     *
     * > largestTriangle(4, 2)
     * 10.392304845413264
     * > largestTriangle(5, 7)
     * 4.639421805988064
     * > largestTriangle(9, 1)
     * 105.2220865598093
     */
    public static double largestTriangle(int a, int b) {
        if (b <= 0 || a < 0) {
            return -1;
        }
        if (a == b) {
            return 0;
        }
        double area = (3 * Math.sqrt(3) * Math.pow(a, 2)) / (4 * b);
        if (area < 0) {
            area = 0;
        }
        return area;
    }
}

