import java.math.*;


class FirstRepeatedChar {
    /**
     * * Write a Java function to find the first repeated character in a given string.
     *
     * > firstRepeatedChar("Google")
     * "o"
     * > firstRepeatedChar("data")
     * "a"
     * > firstRepeatedChar("python")
     * "\x00"
     */
    public static String firstRepeatedChar(String str) {
        HashMap<Character, Integer> charCountMap = new HashMap<>();
        for (char c : str.toCharArray()) {
            if (charCountMap.containsKey(c)) {
                return String.valueOf(c); 
            } else {
                charCountMap.put(c, 1);
            }
        }
        return "\0";
    }
}

