import java.io.*;
import java.lang.*;
import java.util.*;
import java.math.*;


class MaxSum {
    /**
     * * Write a function to find the maximum sum of bi-tonic sub-sequence for the given array.
     *
     * > maxSum([1, 15, 51, 45, 33, 100, 12, 18, 9], 9)
     * 194
     * > maxSum([80, 60, 30, 40, 20, 10], 6)
     * 210
     * > maxSum([2, 3, 14, 16, 21, 23, 29, 30], 8)
     * 138
     */
    public static int maxSum(List<Integer> arr, int n) {
        // Create two arrays and initialize as 0
        int[] MSIBS = new int[n];
        int[] MSDBS = new int[n];

        for (int i = 0; i < n; i++) {
            MSIBS[i] = arr.get(i);
            MSDBS[i] = arr.get(i);
        }

        // Construct increasing sequence array
        for (int i = 1; i < n; i++)
            for (int j = 0; j < i; j++)
                if (arr.get(i) > arr.get(j) && MSIBS[i] < MSIBS[j] + arr.get(i))
                    MSIBS[i] = MSIBS[j] + arr.get(i);

        // Construct decreasing sequence array
        for (int i = n - 2; i >= 0; i--)
            for (int j = n - 1; j > i; j--)
                if (arr.get(i) > arr.get(j) && MSDBS[i] < MSDBS[j] + arr.get(i))
                    MSDBS[i] = MSDBS[j] + arr.get(i);

        // Find maximum value
        int max = MSIBS[0] + MSDBS[0] - arr.get(0);

        for (int i = 1; i < n; i++)
            if (max < MSIBS[i] + MSDBS[i] - arr.get(i))
                max = MSIBS[i] + MSDBS[i] - arr.get(i);

        return max;
    }
}

