import java.util.*;
import java.math.*;


class ModularInverse {
    /**
     * * Write a function to count array elements having modular inverse under given prime number p equal to itself.
     *
     * > modularInverse([1, 6, 4, 5], 4, 7)
     * 2
     * > modularInverse([1, 3, 8, 12, 12], 5, 13)
