vector<int> largeProduct(vector<int> nums1, vector<int> nums2, int n) {
{
// Your code here
    priority_queue<int> pq;
    for (auto val1 : nums1) {
        for (auto val2 : nums2) {
            int product = val1 * val2;
            if (pq.size() < n) pq.push(product);
            else if (pq.top() < product) {
                pq.pop();
                pq.push(product);
            }
        }
    }

    vector<int> result;
    while (!pq.empty()) {
        result.push_back(pq.top());
        pq.pop();
    }

    reverse(result.begin(), result.end());

    return result;
}

