     */
    public static int sumDifference(int n) {
        int sum1 = 0;
        int sum2 = 0;
        for (int i = 0; i <= n; i++) {
            sum1 += i;
            sum2 += (i * i);
        }
        int diff = (int) (Math.pow(sum1, 2) - sum2);
        return diff;
    }
}

