     */
    public static int sumThreeSmallestNums(List<Integer> lst) {
        int ret = 0;

        List<Integer> sorted = new ArrayList<Integer>();
        for (int x : lst) {
            if (x > 0) {
                sorted.add(x);
            }
        }

        Collections.sort(sorted);

        for (int i = 0; i < 3; i++) {
            ret += sorted.get(i);
        }

        return ret;
    }
}

