vector<int> findMinimumRange(vector<vector<int>> list) {
{
   compareElem c;
    priority_queue<Elem, vector<Elem>, compareElem> pq;
    int maxVal = INT_MIN;
    int range = INT_MAX;
    pair<int, int> rangeIndices = {0, 0};
    for (int i = 0; i < list.size(); i++) {
        Elem e = {list[i][0], 0, i};
        pq.push(e);
        maxVal = max(maxVal, list[i][0]);
    }

    while (true) {
        Elem curr = pq.top();
        pq.pop();

        int currMinVal = curr.val;
        int listNum = curr.listNum;
        if (range > maxVal - currMinVal + 1) {
            range = maxVal - currMinVal + 1;
            rangeIndices = {currMinVal, maxVal};
        }

        if (curr.idx + 1 < list[listNum].size()) {
            curr.val = list[listNum][curr.idx + 1];
            curr.idx += 1;
            maxVal = max(maxVal, curr.val);
