     */
    public static int minimumLength(String s) {
        char[] charArray = s.toCharArray();
        int length = 0;
        for (int i = 0; i < charArray.length; i++) {
            if (charArray[i] == ' ') {
                length++;
            } else {
                if (i > 0 && charArray[i - 1] != ' ' && i + 1 < charArray.length && charArray[i + 1] != ' ') {
                    length++;
                }
            }
        }
        return length;
    }
}

