import java.io.*;
import java.lang.*;
import java.util.*;
import java.math.*;


class ConvertListDictionary {
    /**
     * * Write a function to convert more than one list to nested dictionary.
     *
     * > convertListDictionary(["S001", "S002", "S003", "S004"], ["Adina Park", "Leyton Marsh", "Duncan Boyle", "Saim Richards"], [85, 98, 89, 92])
     * [{"S001": {"Adina Park": 85}}, {"S002": {"Leyton Marsh": 98}}, {"S003": {"Duncan Boyle": 89}}, {"S004": {"Saim Richards": 92}}]
     * > convertListDictionary(["abc", "def", "ghi", "jkl"], ["python", "program", "language", "programs"], [100, 200, 300, 400])
     * [{"abc": {"python": 100}}, {"def": {"program": 200}}, {"ghi": {"language": 300}}, {"jkl": {"programs": 400}}]
     * > convertListDictionary(["A1", "A2", "A3", "A4"], ["java", "C", "C++", "DBMS"], [10, 20, 30, 40])
     * [{"A1": {"java": 10}}, {"A2": {"C": 20}}, {"A3": {"C++": 30}}, {"A4": {"DBMS": 40}}]
     */
    public static List<HashMap<String, HashMap<String, Integer>>> convertListDictionary(List<String> l1, List<String> l2, List<Integer> l3) {
        List<HashMap<String, HashMap<String, Integer>>> nestedList = new ArrayList<>();
        for (int i = 0; i < l1.size(); i++) {
            HashMap<String, Integer> innerMap = new HashMap<>();
            innerMap.put(l2.get(i), l3.get(i));

            HashMap<String, HashMap<String, Integer>> outerMap = new HashMap<>();
            outerMap.put(l1.get(i), innerMap);

            nestedList.add(outerMap);
        }
        return nestedList;
    }
}

