/**
 * Write a function to find k number of pairs which consist of one element from the first array and one element from the second array.
 * > kSmallestPairs(vector<int>{1, 3, 7}, vector<int>{2, 4, 6}, 2)
 * {{1, 2}, {1, 4}}
 * > kSmallestPairs(vector<int>{1, 3, 7}, vector<int>{2, 4, 6}, 1)
 * {{1, 2}}
 * > kSmallestPairs(vector<int>{1, 3, 7}, vector<int>{2, 4, 6}, 7)
 * {{1, 2}, {1, 4}, {3, 2}, {1, 6}, {3, 4}, {3, 6}, {7, 2}}
 */
vector<vector<int>> kSmallestPairs(vector<int> nums1, vector<int> nums2, int k) {
{
    auto comp = [&nums1, &nums2](pair<int, int> a, pair<int, int> b) {
        return nums1[a.first] + nums2[a.second] > nums1[b.first] + nums2[b.second];
    };

    priority_queue<pair<int, int>, vector<pair<int, int>>, decltype(comp)> minHeap(comp);
    vector<vector<int>> result;

    if (nums1.empty() || nums2.empty() || k <= 0)
        return result;

    for (int i = 0; i < nums1.size() && i < k; ++i)
        minHeap.push({i, 0});

    while (k-- > 0 && !minHeap.empty()) {
        auto idx_pair = minHeap.top(); 
        minHeap.pop();
        result.push_back({nums1[idx_pair.first], nums2[idx_pair.second]});

        if (idx_pair.second == nums2.size() - 1) 
            continue;

        minHeap.push({idx_pair.first, idx_pair.second + 1});
    }
    return result;
}

