 * > shellSort(vector<int>{32, 30, 16, 96, 82, 83, 74})
 * {16, 30, 32, 74, 82, 83, 96}
 */
vector<int> shellSort(vector<int> myList) {
{
    // Write your code here
    int n = myList.size();
    int temp;
    for (int gap = n / 2; gap > 0; gap /= 2) {
        for (int i = gap; i < n; i++) {
            int j = i;
            while (j >= gap && myList[j - gap] > myList[j]) {
                temp = myList[j];
                myList[j] = myList[j - gap];
                myList[j - gap] = temp;
                j -= gap;
            }
        }
    }
    return myList;
}

