            @Override
            public int compare(List<Integer> o1, List<Integer> o2) {
                int result = 0;
                for (int i = 0; i < o1.size(); i++) {
                    result = result + o1.get(i) - o2.get(i);
                }
                return result;
            }
        });
        return res;
    }
}

