class CountCommon {
    /**
     * * Write a function to count the most common words in a dictionary.
     *
     * > countCommon(["red", "green", "black", "pink", "black", "white", "black", "eyes", "white", "black", "orange", "pink", "pink", "red", "red", "white", "orange", "white", "black", "pink", "green", "green", "pink", "green", "pink", "white", "orange", "orange", "red"])
     * [["pink", 6], ["black", 5], ["white", 5], ["red", 4]]
     * > countCommon(["one", "two", "three", "four", "five", "one", "two", "one", "three", "one"])
     * [["one", 4], ["two", 2], ["three", 2], ["four", 1]]
     * > countCommon(["Facebook", "Apple", "Amazon", "Netflix", "Google", "Apple", "Netflix", "Amazon"])
     * [["Apple", 2], ["Amazon", 2], ["Netflix", 2], ["Facebook", 1]]
     */
    public static List<List<Object>> countCommon(List<String> words) {
{
        Map<String, Integer> freqMap = new HashMap<>();

        for (String word : words) {
            freqMap.put(word, freqMap.getOrDefault(word, 0) + 1);
        }

        PriorityQueue<Map.Entry<String, Integer>> pq = new PriorityQueue<>((a, b) -> {
            if (a.getValue().equals(b.getValue())) {
                return b.getKey().compareTo(a.getKey());
            } else {
                return a.getValue() - b.getValue();
            }
        });

        for (Map.Entry<String, Integer> entry : freqMap.entrySet()) {
            pq.offer(entry);
            if (pq.size() > 4) {
                pq.poll();
            }
        }

        List<List<Object>> result = new ArrayList<>();
        while (!pq.isEmpty()) {
            List<Object> subList = new ArrayList<>();
            Map.Entry<String, Integer> entry = pq.poll();
            subList.add(0, entry.getKey());
            subList.add(1, entry.getValue());
            result.add(0, subList);
        }

        return result;
    }
}

