/**
 * Write a function to sort a given matrix in ascending order according to the sum of its rows.
 * > sortMatrix(vector<vector<int>>{{1, 2, 3}, {2, 4, 5}, {1, 1, 1}})
 * {{1, 1, 1}, {1, 2, 3}, {2, 4, 5}}
 * > sortMatrix(vector<vector<int>>{{1, 2, 3}, {-2, 4, -5}, {1, -1, 1}})
 * {{-2, 4, -5}, {1, -1, 1}, {1, 2, 3}}
 * > sortMatrix(vector<vector<int>>{{5, 8, 9}, {6, 4, 3}, {2, 1, 4}})
 * {{2, 1, 4}, {6, 4, 3}, {5, 8, 9}}
 */
vector<vector<int>> sortMatrix(vector<vector<int>> m) {

// Sort matrix rows based on their sum.
    sort(m.begin(), m.end(), [](const vector<int> & a, const vector<int> & b) {
        return accumulate(a.begin(), a.end(), 0) < accumulate(b.begin(), b.end(), 0);
    });

    return m;
}

