/**
 * Write a c++ function to remove all digits from a list of strings.
 * > remove(vector<string>{string("4words"), string("3letters"), string("4digits")})
 * {string("words"), string("letters"), string("digits")}
 * > remove(vector<string>{string("28Jan"), string("12Jan"), string("11Jan")})
 * {string("Jan"), string("Jan"), string("Jan")}
 * > remove(vector<string>{string("wonder1"), string("wonder2"), string("wonder3")})
 * {string("wonder"), string("wonder"), string("wonder")}
 */
vector<string> remove(vector<string> list) {
    vector<string> result;
    for(string s: list) {
        string tmp;
        for(char c: s) {
            if(isdigit(c)) {
                continue;
            } else {
                tmp += c;
            }
        }
        result.push_back(tmp);
    }
    return result;
}

