 * > countSubstrings(string("112112"), 6)
 * 6
 * > countSubstrings(string("111"), 3)
 * 6
 * > countSubstrings(string("1101112"), 7)
 * 12
 */
int countSubstrings(string s, int n) {
    // initializing count variable
    int count = 0;

    // loop to calculate all substrings
    for (int i = 0; i < n; i++) {
        int sum = 0;

        // length of substring
        for (int len = 1; i + len <= n; len++) {
            sum += (s[i + len - 1] - '0'); // deducting ascii value of '0' to get integer value

            // if length equals to sum, then increment the count
            if (sum == len)
                count++;
        }
    }
    return count;
}

