 * > findTuples(vector<vector<int>>{{7, 9, 16}, {8, 16, 4}, {19, 17, 18}}, 4)
 * string("[(8, 16, 4)]")
 */
string findTuples(vector<vector<int>> testList, int k) {
    vector<vector<int>> res;
    for (auto &vec : testList) {
        if(all_of(vec.begin(), vec.end(), [k](int i){return i % k == 0;})) {
            res.push_back(vec);
        }
    }

    // Convert vector to string
    string result = "[";
    for (auto &vec : res) {
        result += "(";
        for (int i = 0; i < vec.size(); i++) {
            result += to_string(vec[i]);
            if (i != vec.size()-1)
                result += ", ";
        }
        result += "), ";
    }
    if (result.size() > 2)
        result.erase(result.size()-2); // Erase trailing comma and space
    result += "]";
    return result;
}

