/**
 * Write a function to zip the two given tuples.
 * > zipTuples(vector<int>{7, 8, 4, 5, 9, 10}, vector<int>{1, 5, 6})
 * {{7, 1}, {8, 5}, {4, 6}, {5, 1}, {9, 5}, {10, 6}}
 * > zipTuples(vector<int>{8, 9, 5, 6, 10, 11}, vector<int>{2, 6, 7})
 * {{8, 2}, {9, 6}, {5, 7}, {6, 2}, {10, 6}, {11, 7}}
 * > zipTuples(vector<int>{9, 10, 6, 7, 11, 12}, vector<int>{3, 7, 8})
 * {{9, 3}, {10, 7}, {6, 8}, {7, 3}, {11, 7}, {12, 8}}
 */
vector<vector<int>> zipTuples(vector<int> testTup1, vector<int> testTup2) {
  vector<vector<int>> res;
  vector<int> temp;
  int i = 0;

  while (testTup1.size() > i || testTup2.size() > i) {
    temp.push_back(testTup1[i % testTup1.size()]);
    temp.push_back(testTup2[i % testTup2.size()]);
    res.push_back(temp);
    temp.clear();
    i++;
  }
  return (res);
}

