  for (i = 0; i < nums.size() - 1; ++i) {
    for (j = i + 1; j < nums.size(); ++j) {
      if (nums[i] > nums[j]) {
        int temp = nums[i];
        nums[i] = nums[j];
        nums[j] = temp;
      }
    }
  }
  return nums;
}

