 * > isMajority(vector<int>{1, 2, 3, 3, 3, 3, 10}, 7, 3)
 * true
 * > isMajority(vector<int>{1, 1, 2, 4, 4, 4, 6, 6}, 8, 4)
 * false
 * > isMajority(vector<int>{1, 1, 1, 2, 2}, 5, 1)
 * true
 */
bool isMajority(vector<int> arr, int n, int x) {
    int count = 0;
    int prev = -1;
    for (int i = 0; i < n; ++i) {
