 * {string("assert"), string("atement")}
 */
std::vector<std::string> wordsAe(const std::string& text) {
    std::vector<std::string> res;
    int len = text.length();
    for (int i = 0; i < len; i++) {
        if (text[i] == 'a' || text[i] == 'e') {
            std::string substr = text.substr(i, 2);
            int j = i + 2;
            while (j < len && text[j] != ' ') {
                substr += text[j];
                j++;
            }
            if (j - i > 1) {
                res.push_back(substr);
            }
            i = j;
        }
    }
    return res;
}

