/**
 * Write a function to find the smallest range that includes at-least one element from each of the given arrays.
 * > findMinimumRange(vector<vector<int>>{{3, 6, 8, 10, 15}, {1, 5, 12}, {4, 8, 15, 16}, {2, 6}})
 * {4, 6}
 * > findMinimumRange(vector<vector<int>>{{2, 3, 4, 8, 10, 15}, {1, 5, 12}, {7, 8, 15, 16}, {3, 6}})
 * {4, 7}
 * > findMinimumRange(vector<vector<int>>{{4, 7, 9, 11, 16}, {2, 6, 13}, {5, 9, 16, 17}, {3, 7}})
 * {5, 7}
 */
vector<int> findMinimumRange(vector<vector<int>> list) {
    auto comp = [&](pair<int, int>& p1, pair<int, int>& p2) {
        return list[p1.first][p1.second] > list[p2.first][p2.second];
    };
    priority_queue<pair<int, int>, vector<pair<int, int>>, decltype(comp)> queue(comp);
    int max_val = numeric_limits<int>::min(), min_val, range = numeric_limits<int>::max();
    vector<int> rangeIndices(2);

    for (int i = 0; i < list.size(); i++) {
        queue.push(make_pair(i, 0));
        max_val = max(max_val, list[i][0]);
    }

    while (queue.size() == list.size()) {
        auto curr = queue.top();
        queue.pop();
        min_val = list[curr.first][curr.second];

        if (max_val - min_val < range) {
            range = max_val - min_val;
            rangeIndices[0] = min_val;
            rangeIndices[1] = max_val;
        }

        if (++curr.second < list[curr.first].size()) {
            queue.push(curr);
            max_val = max(max_val, list[curr.first][curr.second]);
        }
    }

    return rangeIndices;
}

