/**
 * Write a function to solve gold mine problem.
 * > getMaxgold(vector<vector<int>>{{1, 3, 1, 5}, {2, 2, 4, 1}, {5, 0, 2, 3}, {0, 6, 1, 2}}, 4, 4)
 * 16
 * > getMaxgold(vector<vector<int>>{{10, 20}, {30, 40}}, 2, 2)
