 * > getMaxgold(vector<vector<int>>{{4, 9}, {3, 7}}, 2, 2)
 * 13
 */
int getMaxgold(vector<vector<int>> gold, int m, int n) {
    vector<vector<int>> goldMine(gold);
    for (int col=n-1; col>=0; col--)
    {
        for (int row=0; row<m; row++)
        {
            int right = (col==n-1)? 0: goldMine[row][col+1];
            int right_up = (row==0 || col==n-1)? 0: goldMine[row-1][col+1];
            int right_down = (row==m-1 || col==n-1)? 0: goldMine[row+1][col+1];
            goldMine[row][col] = gold[row][col] + max(right, max(right_up, right_down));
        }
    }

    int res = goldMine[0][0];
    for (int i=1; i<m; i++)
        res = max(res, goldMine[i][0]);
    return res;
}

