 * Write a function to sort the given array by using shell sort.
 * > shellSort(vector<int>{12, 23, 4, 5, 3, 2, 12, 81, 56, 95})
 * {2, 3, 4, 5, 12, 12, 23, 56, 81, 95}
 * > shellSort(vector<int>{24, 22, 39, 34, 87, 73, 68})
