/**
 * Write a function to search an element in the given array by using binary search.
 * > binarySearch(vector<int>{1, 2, 3, 5, 8}, 6)
 * false
 * > binarySearch(vector<int>{7, 8, 9, 10, 13}, 10)
 * true
 * > binarySearch(vector<int>{11, 13, 14, 19, 22, 36}, 23)
 * false
 */
bool binarySearch(vector<int> itemList, int item) {
    int start = 0, end = itemList.size() - 1;
    while (start <= end) {
        int mid = (start + end) / 2;
        if (itemList[mid] == item) {
            return true;
        }
        if (itemList[mid] > item) {
            end = mid - 1;
        } else {
            start = mid + 1;
        }
    }
    return false;
}

