/**
 * Write a function to find the number of subsequences having product smaller than k for the given non negative array.
 * > noOfSubsequences(vector<int>{1, 2, 3, 4}, 10)
 * 11
 * > noOfSubsequences(vector<int>{4, 8, 7, 2}, 50)
 * 9
 * > noOfSubsequences(vector<int>{5, 6, 7, 8}, 15)
 * 4
 */
int noOfSubsequences(vector<int> arr, int k) {
    int n = arr.size();
    vector<vector<int>> dp(k + 1, vector<int> (n + 1, 0));
    for (int i = 1; i <= n; i++) {
        dp[0][i] = 1;
    }

    for (int i = 1; i <= k; i++) {
        for (int j = 1; j <= n; j++) {
            dp[i][j] = dp[i][j - 1];

            if (arr[j - 1] <= i && arr[j - 1] > 0) {
                dp[i][j] += dp[i/arr[j-1]][j-1] + 1;
            }
        }
    }
    return dp[k][n];
}

