     */
    public static int parallelogramArea(int b, int h) {
        if (b > 0 && h > 0) {
            return b * h;
        } else if (b > 0 && h < 0) {
            return 1 + parallelogramArea(b - 1, h + 1);
        } else {
            return 1 + parallelogramArea(b, h - 1);
        }
    }
}

