import java.io.*;
import java.lang.*;
import java.util.*;
import java.math.*;


class EncodeList {
    /**
     * * Write a function to reflect the run-length encoding from a list.
     *
     * > encodeList([1, 1, 2, 3, 4, 4.3, 5, 1])
     * [[2, 1], [1, 2], [1, 3], [1, 4], [1, 4.3], [1, 5], [1, 1]]
     * > encodeList("automatically")
     * [[1, "a"], [1, "u"], [1, "t"], [1, "o"], [1, "m"], [1, "a"], [1, "t"], [1, "i"], [1, "c"], [1, "a"], [2, "l"], [1, "y"]]
     * > encodeList("python")
     * [[1, "p"], [1, "y"], [1, "t"], [1, "h"], [1, "o"], [1, "n"]]
     */
    public static List<List<Object>> encodeList(Object list1) {
        ArrayList list1Array = list1 instanceof String ? new ArrayList(Arrays.asList(((String) list1).split(""))) : new ArrayList((List) list1);
        List<List<Object>> result = new ArrayList<>();
        for (int i = 0; i < list1Array.size(); ) {
            int count = 0;
            for (int j = i; j < list1Array.size(); j++) {
                if (!list1Array.get(i).equals(list1Array.get(j))) {
                    break;
                }
                count++;
            }
            result.add(Arrays.asList(count, list1Array.get(i)));
            i += count;
        }
        return result;
    }
}

