import java.io.*;
import java.lang.*;
import java.util.*;
import java.math.*;


class GroupKeyvalue {
    /**
     * * Write a function to group a sequence of key-value pairs into a dictionary of lists.
     *
     * > groupKeyvalue([["yellow", 1], ["blue", 2], ["yellow", 3], ["blue", 4], ["red", 1]])
     * {"yellow": [1, 3], "blue": [2, 4], "red": [1]}
     * > groupKeyvalue([["python", 1], ["python", 2], ["python", 3], ["python", 4], ["python", 5]])
     * {"python": [1, 2, 3, 4, 5]}
     * > groupKeyvalue([["yellow", 100], ["blue", 200], ["yellow", 300], ["blue", 400], ["red", 100]])
     * {"yellow": [100, 300], "blue": [200, 400], "red": [100]}
     */
    public static HashMap<String, List<Integer>> groupKeyvalue(List<List<Object>> l) {
        HashMap<String, List<Integer>> hmap = new HashMap<>();
        for (List<Object> innerList : l) {
            String key = (String)innerList.get(0);
            Integer value = (Integer)innerList.get(1);

            if (hmap.containsKey(key)) {
                hmap.get(key).add(value);
            } else {
                List<Integer> list = new ArrayList<>();
                list.add(value);
                hmap.put(key, list);
            }
        }
        return hmap;
    }
}

