class CheckOccurences {
    /**
     * * Write a function to check the occurrences of records which occur similar times in the given tuples.
     *
     * > checkOccurences([[3, 1], [1, 3], [2, 5], [5, 2], [6, 3]])
     * {[1, 3]: 2, [2, 5]: 2, [3, 6]: 1}
     * > checkOccurences([[4, 2], [2, 4], [3, 6], [6, 3], [7, 4]])
     * {[2, 4]: 2, [3, 6]: 2, [4, 7]: 1}
     * > checkOccurences([[13, 2], [11, 23], [12, 25], [25, 12], [16, 23]])
     * {[2, 13]: 1, [11, 23]: 1, [12, 25]: 2, [16, 23]: 1}
     */
    public static HashMap<List<Integer>, Integer> checkOccurences(List<List<Integer>> testList) {
        HashMap<List<Integer>, Integer> map = new HashMap<>();
        for (List<Integer> pair : testList) {
            Collections.sort(pair);
            map.put(pair, map.getOrDefault(pair, 0) + 1);
        }
        return map;
    }
}

