        for (int i = 0; i < dlist.size(); i++) {
            if (item == dlist.get(i)) {
                found = true;
                break;
            }
        }
        return found ? Arrays.asList(true, dlist.indexOf(item)) : Arrays.asList(false, -1);
    }
}

