import java.io.*;
import java.lang.*;
import java.util.*;
import java.math.*;


class DictDepth {
    /**
     * * Write a function to find the depth of a dictionary.
     *
     * > dictDepth({"a": 1, "b": {"c": {"d": {}}}})
     * 4
     * > dictDepth({"a": 1, "b": {"c": "python"}})
     * 2
     * > dictDepth({1: "Sun", 2: {3: {4: "Mon"}}})
     * 3
     */
    public static int dictDepth(Object d) {
        if (!(d instanceof Map)) {
            return 0;
        }

        Map<?, ?> dictionary = (Map<?, ?>) d;

        int maxDepth = 0;

        for (Object key : dictionary.keySet()) {
            int currentDepth = dictDepth(dictionary.get(key));
            if (currentDepth > maxDepth) {
                maxDepth = currentDepth;
            }
        }

        return maxDepth + 1;
    }
}

