import java.io.*;
import java.lang.*;
import java.util.*;
import java.math.*;


class MaxSumIncreasingSubseq {
    /**
     * * Write a function to find the maximum sum of increasing subsequence from prefix till ith index and also including a given kth element which is after i, i.e., k > i .
     *
     * > maxSumIncreasingSubseq([1, 101, 2, 3, 100, 4, 5], 7, 4, 6)
     * 11
     * > maxSumIncreasingSubseq([1, 101, 2, 3, 100, 4, 5], 7, 2, 5)
     * 7
     * > maxSumIncreasingSubseq([11, 15, 19, 21, 26, 28, 31], 7, 2, 4)
     * 71
     */
    public static int maxSumIncreasingSubseq(List<Integer> a, int n, int index, int k) {
        int dp[][] = new int[n][n];
        for (int i = 0; i < n; i++) {
            if (a.get(i) > a.get(0)) {
                dp[0][i] = a.get(i) + a.get(0);
            } else {
                dp[0][i] = a.get(i);
            }
        }
        for (int i = 1; i < n; i++) {
            for (int j = 0; j < n; j++) {
                if (a.get(j) > a.get(i) && j > i) {
                    if ((dp[i - 1][i] + a.get(j)) > dp[i - 1][j]) {
                        dp[i][j] = dp[i - 1][i] + a.get(j);
                    } else {
                        dp[i][j] = dp[i - 1][j];
                    }
                } else {
                    dp[i][j] = dp[i - 1][j];
                }
            }
        }
        return dp[index][k];
    }
}

