import java.io.*;
import java.lang.*;
import java.util.*;
import java.math.*;


class ModifiedEncode {
    /**
     * * Write a function to reflect the modified run-length encoding from a list.
     *
     * > modifiedEncode([1, 1, 2, 3, 4, 4, 5, 1])
     * [[2, 1], 2, 3, [2, 4], 5, 1]
     * > modifiedEncode("automatically")
     * ["a", "u", "t", "o", "m", "a", "t", "i", "c", "a", [2, "l"], "y"]
     * > modifiedEncode("python")
     * ["p", "y", "t", "h", "o", "n"]
     */
    public static List<Object> modifiedEncode(Object alist) {
        List<Object> output = new ArrayList<>();
        int count = 1;

        List<Object> list;
        if (alist instanceof String) {
            list = Arrays.asList(((String) alist).split(""));
        } else {
            list = (List<Object>) alist;
        }

        for (int i = 1; i < list.size(); i++) {
            if (list.get(i).equals(list.get(i - 1))) {
                count++;
                if (i == list.size() - 1) {
                    output.add(Arrays.asList(count, list.get(i)));
                }
            } else {
                if (count > 1) {
                    output.add(Arrays.asList(count, list.get(i - 1)));
                    count = 1;
                } else {
                    output.add(list.get(i - 1));
                }
            }

            if (i == list.size() - 1 && !list.get(i).equals(list.get(i - 1))) {
                output.add(list.get(i));
            }
        }

        if(list.size() == 1){
            output.add(list.get(0));
        }

        return output;
    }
}

