import java.io.*;
import java.lang.*;
import java.util.*;
import java.math.*;


class ReArrange {
    /**
     * * Write a function to re-arrange the given array in alternating positive and negative items.
     *
     * > reArrange([-5, -2, 5, 2, 4, 7, 1, 8, 0, -8], 10)
     * [-5, 5, -2, 2, -8, 4, 7, 1, 8, 0]
     * > reArrange([1, 2, 3, -4, -1, 4], 6)
     * [-4, 1, -1, 2, 3, 4]
     * > reArrange([4, 7, 9, 77, -4, 5, -3, -9], 8)
     * [-4, 4, -3, 7, -9, 9, 77, 5]
     */
  public static List<Integer> rightRotate(List<Integer> arr, int n, int out_of_place, int cur) {
    int temp = arr.get(cur);
    for (int i = cur; i > out_of_place; i--) {
      arr.set(i, arr.get(i - 1));
    }
    arr.set(out_of_place, temp);
    return arr;
  }

  public static List<Integer> reArrange(List<Integer> arr, int n) {
    int out_of_place = -1;
    for (int index = 0; index < n; index++) {
      if (out_of_place >= 0) {
        if ((arr.get(index) >= 0 && arr.get(out_of_place) < 0) ||
            (arr.get(index) < 0 && arr.get(out_of_place) >= 0)) {
          arr = rightRotate(arr, n, out_of_place, index);
          if (index - out_of_place > 2) {
            out_of_place += 2;
          } else {
            out_of_place = -1;
          }
        }
      }

      if (out_of_place == -1) {
        if (index % 2 == 0 && arr.get(index) >= 0 || 
            index % 2 == 1 && arr.get(index) < 0) {
          out_of_place = index;
        }
      }
    }

    return arr;
  }
}

