import java.io.*;
import java.lang.*;
import java.util.*;
import java.math.*;


class FindMinimumRange {
    /**
     * * Write a function to find the smallest range that includes at-least one element from each of the given arrays.
     *
     * > findMinimumRange([[3, 6, 8, 10, 15], [1, 5, 12], [4, 8, 15, 16], [2, 6]])
     * [4, 6]
     * > findMinimumRange([[2, 3, 4, 8, 10, 15], [1, 5, 12], [7, 8, 15, 16], [3, 6]])
     * [4, 7]
     * > findMinimumRange([[4, 7, 9, 11, 16], [2, 6, 13], [5, 9, 16, 17], [3, 7]])
     * [5, 7]
     */
    public static List<Integer> findMinimumRange(List<List<Integer>> list) {
        PriorityQueue<int[]> queue = new PriorityQueue<>(Comparator.comparingInt(a -> list.get(a[0]).get(a[1])));
        int max = Integer.MIN_VALUE, min = Integer.MAX_VALUE, range = Integer.MAX_VALUE;
        int[] rangeIndices = new int[2];

        for (int i = 0; i < list.size(); i++) {
            queue.offer(new int[]{i, 0});
            max = Math.max(max, list.get(i).get(0));
        }

        while (queue.size() == list.size()) {
            int[] curr = queue.poll();
            min = list.get(curr[0]).get(curr[1]);

            if (max - min < range) {
                range = max - min;
                rangeIndices[0] = min;
                rangeIndices[1] = max;
            }

            if (++curr[1] < list.get(curr[0]).size()) {
                queue.offer(curr);
                max = Math.max(max, list.get(curr[0]).get(curr[1]));
            }
        }

        return Arrays.asList(rangeIndices[0], rangeIndices[1]);
    }
}

