class AddString {
    /**
     * * Write a function to insert a given string at the beginning of all items in a list.
     *
     * > addString([1, 2, 3, 4], "temp{0}")
     * ["temp1", "temp2", "temp3", "temp4"]
     * > addString(["a", "b", "c", "d"], "python{0}")
     * ["pythona", "pythonb", "pythonc", "pythond"]
     * > addString([5, 6, 7, 8], "string{0}")
     * ["string5", "string6", "string7", "string8"]
     */
    public static List<String> addString(List<Object> list, String string) {
        List<String> output = new ArrayList<>();
        for (Object item : list) {
            output.add(string.replace("{0}", item.toString()));
        }
        return output;
    }
}

