import java.io.*;
import java.lang.*;
import java.util.*;
import java.math.*;


class FirstNonRepeatingCharacter {
    /**
     * * Write a Java function to find the first non-repeated character in a given string.
     *
     * > firstNonRepeatingCharacter("abcabc")
     * null
     * > firstNonRepeatingCharacter("abc")
     * "a"
     * > firstNonRepeatingCharacter("ababc")
     * "c"
     */
    public static String firstNonRepeatingCharacter(String str1) {
        Map<Character, Integer> charMap = new LinkedHashMap<>();
        for (char c : str1.toCharArray()) {
            charMap.put(c, charMap.getOrDefault(c, 0) + 1);
        }

        for (Map.Entry<Character, Integer> entry : charMap.entrySet()) {
            if (entry.getValue() == 1) {
                return String.valueOf(entry.getKey());
            }
        }
        return null;
    }
}

