    public static List<Integer> bitwiseXor(List<Integer> testTup1, List<Integer> testTup2) {
        if (testTup1.size() != testTup2.size()) {
            throw new IllegalArgumentException("testTup1 > testTup2 : " + testTup1.size() + " != " + testTup2.size());
