import java.io.*;
import java.lang.*;
import java.util.*;
import java.math.*;


class GetMaxgold {
    /**
     * * Write a function to solve gold mine problem.
     *
     * > getMaxgold([[1, 3, 1, 5], [2, 2, 4, 1], [5, 0, 2, 3], [0, 6, 1, 2]], 4, 4)
     * 16
     * > getMaxgold([[10, 20], [30, 40]], 2, 2)
     * 70
     * > getMaxgold([[4, 9], [3, 7]], 2, 2)
     * 13
     */
    public static int getMaxgold(List<List<Integer>> gold, int m, int n) {
int[][] goldTable = new int[m][n];
for(int[] rows:goldTable)
    Arrays.fill(rows, 0);

for (int col=n-1; col>=0; col--) {
    for (int row=0; row<m; row++) {
        int right = (col==n-1) ? 0 : goldTable[row][col+1];
        int right_up = (row==0 || col==n-1) ? 0 : goldTable[row-1][col+1];
        int right_down = (row==m-1 || col==n-1) ? 0 : goldTable[row+1][col+1];
        goldTable[row][col] = gold.get(row).get(col) + Math.max(right, Math.max(right_up, right_down));
    }
}
int res = goldTable[0][0];
for (int i=1; i<m; i++)
    res = Math.max(res, goldTable[i][0]);

return res;
}}

