        for (int i = 0; i < testList.size(); i++) {
            for (int j = i + 1; j < testList.size(); j++) {
                if (testList.get(j).get(0) == testList.get(i).get(1) && testList.get(i).get(1) == testList.get(j).get(0)) {
                    res += 1;
                }
            }
        }
