     */
    public static int findRemainder(List<Integer> arr, int lens, int n) {
        int rem = 0;
        int temp = 1;
        for (int i = 0; i < lens; i++) {
            temp = temp * arr.get(i);
        }
        rem = temp % n;
        return rem;
    }
}

